mapper.update:
update t_course
set name = #{name},is_show = #{isShow}
where id=#{id};

mapper.select:
select id,name,create_date as createDate,is_show as isShow, (select username from t_user where id = c.user_id ) as creator, (SELECT count(*) from t_tag where course_id = c.id) as tagQty, (select count(*) from t_question where course_id = c.id) as questionQty, (select count(*) from t_catalog where course_id = c.id) as catalogQty from t_course c
AND is_show = #{queryParams.status}
limit #{offset},#{pageSize}
